<?php

namespace publiq\structuredData\twigextensions;

use CultuurNet\CalendarSummaryV3\CalendarPlainTextFormatter;

class PubliqFormatterTwigExtension extends \Twig_Extension
{
    public function getName()
    {
        return 'Publiq Date Formatter';
    }

    public function getFilters() {
        return [
            new \Twig_SimpleFilter('dateFormatter', [$this, 'dateFormatFilter']),
            new \Twig_SimpleFilter('getMinAge', [$this, 'minAgeFilter']),
            new \Twig_SimpleFilter('formatThemes', [$this, 'themeFilter'])
        ];
    }

    public function getFunctions() {
        return [
            new \Twig_SimpleFunction('dateFormatter', [$this, 'dateFormatFilter']),
            new \Twig_SimpleFunction('getMinAge', [$this, 'minAgeFilter']),
            new \Twig_SimpleFunction('formatThemes', [$this, 'themeFilter'])
        ];
    }


    public function dateFormatFilter($offer, $length)
    {
        $formatter = new CalendarPlainTextFormatter();
        return nl2br($formatter->format($offer, $length));

    }

    public function minAgeFilter($ageRange)
    {
        $ages = explode("-", $ageRange);
        return $ages[0];
    }

